import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [menuOpen, setMenuOpen] = useState(false);
    const [bioExpanded, setBioExpanded] = useState(false);
    const [visibleSections, setVisibleSections] = useState([]);

    useEffect(() => {
        const handleScroll = () => {
            const sections = document.querySelectorAll('.fade-in-section');
            const newVisibleSections = [];

            sections.forEach((section, index) => {
                const sectionTop = section.getBoundingClientRect().top;
                const windowHeight = window.innerHeight;
                if (sectionTop < windowHeight * 0.75) {
                    newVisibleSections.push(index);
                }
            });

            setVisibleSections(newVisibleSections);
        };

        window.addEventListener('scroll', handleScroll);
        handleScroll(); // Initial check

        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const toggleBio = () => {
        setBioExpanded(!bioExpanded);
    };

    const scrollToSection = (sectionId) => {
        const element = document.getElementById(sectionId);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
            setMenuOpen(false);
        }
    };

    return (
        <div className="App">
            {/* HERO SECTION */}
            <section className="hero-section" id="home">
                <div className="logo">HERO</div>

                <div className={`hamburger ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>

                <nav className={`nav-menu ${menuOpen ? 'active' : 'active'}`}>
                    <ul>
                        <li><a onClick={() => scrollToSection('home')}>Home</a></li>
                        <li><a onClick={() => scrollToSection('about')}>About</a></li>
                        <li><a onClick={() => scrollToSection('contact')}>Contact</a></li>
                        <li><a onClick={() => scrollToSection('gallery')}>Gallery</a></li>
                    </ul>
                </nav>

                <div className="hero-content">
                    <h1>THE<br />HERO<br />MMA<br />&<br />FITNESS<br />GYM</h1>
                    <p>Train like a pro at the top MMA Training Academy in Pune and Nashik</p>
                </div>

                <div className="bottom-banner">
                    <div className="banner-logo">
                        <svg viewBox="0 0 100 100">
                            <circle cx="30" cy="30" r="25" fill="none" stroke="#ff9800" strokeWidth="3" />
                            <circle cx="70" cy="30" r="25" fill="none" stroke="#ff9800" strokeWidth="3" />
                            <rect x="25" y="50" width="50" height="40" fill="none" stroke="#ff9800" strokeWidth="3" />
                        </svg>
                    </div>
                    <div className="banner-text">
                        <h3>The More You Train Hard, The More You Fight Smart</h3>
                        <p>Book your trial now</p>
                    </div>
                </div>
            </section>

            {/* COACH SECTION */}
            <section className={`coach-section fade-in-section ${visibleSections.includes(0) ? 'is-visible' : ''}`} id="about">
                <div className="coach-image">
                    <div className="coach-placeholder">Coach Image Placeholder</div>
                </div>
                <div className="coach-info">
                    <div className="coach-title">COACH</div>
                    <h2 className="coach-name">ZAYEEN KHAN</h2>
                    <div className={`coach-bio ${bioExpanded ? 'expanded' : ''}`}>
                        <p>India's First Professional International MMA Fighter</p>
                        <p>Ex-CISF (Central Industrial Security Force) Serviceman</p>
                        <p>17 years Experience In MMA Industry, Fought And Trained World-Wide.</p>
                        <p>Trained Under International MMA Coaches.</p>
                        <p className="hidden-content">(Legacy Gym) Thailand For Muay-Thai. (China Top Team, China And Brazil) For Brazilian Jiu-Jitsu Russia Etc...</p>
                        <p className="hidden-content">Fought in the Organization name</p>
                        <p className="hidden-content">Art of War) Beijing China.</p>
                        <p className="hidden-content">(Legend Fighting Championship) Hong Kong.</p>
                        <p className="hidden-content">(Phoenix Championship) Manila laranatan.</p>
                        <p className="hidden-content">(Xplozion Fights) Macau Hong Kong</p>
                        <p className="hidden-content">Gold Medalist In National's (Clash Of The Champions) Continue In Your 2015-08 & 17.</p>
                    </div>
                    <span className="read-more" onClick={toggleBio}>
                        {bioExpanded ? 'Read less...' : 'Read more...'}
                    </span>
                </div>
            </section>

            {/* SERVICES SECTION */}
            <section className={`services-section fade-in-section ${visibleSections.includes(1) ? 'is-visible' : ''}`}>
                <h2 className="section-title">The HERO MMA services</h2>
                <div className="services-grid">
                    <ServiceCard
                        title="MMA Training"
                        description="MMA training offers a full-body workout that builds strength, endurance, and discipline. With a focus on striking, grappling, and conditioning, it enhances both physical fitness and mental toughness."
                        gradient="linear-gradient(135deg, #1a3a4a 0%, #2a4a5a 100%)"
                    />
                    <ServiceCard
                        title="Fitness Training"
                        description="Comprehensive fitness programs designed to improve overall health, strength, and endurance."
                        gradient="linear-gradient(135deg, #3a1a4a 0%, #4a2a5a 100%)"
                    />
                    <ServiceCard
                        title="Self Defense"
                        description="Learn practical self-defense techniques that can keep you safe in real-world situations."
                        gradient="linear-gradient(135deg, #4a1a1a 0%, #5a2a2a 100%)"
                    />
                </div>
            </section>

            {/* TESTIMONIALS SECTION */}
            <section className={`testimonials-section fade-in-section ${visibleSections.includes(2) ? 'is-visible' : ''}`}>
                <h2 className="section-title">TESTIMONIALS</h2>
                <div className="testimonials-grid">
                    <TestimonialCard
                        text="Training at HERO MMA has completely transformed my fitness level and confidence. The coaches are world-class!"
                        author="John Doe"
                    />
                    <TestimonialCard
                        text="Best MMA academy in Pune! The facilities are top-notch and the training methodology is exceptional."
                        author="Jane Smith"
                    />
                    <TestimonialCard
                        text="Coach Zayeen's expertise and dedication to teaching is unmatched. Highly recommend for serious fighters!"
                        author="Mike Johnson"
                    />
                    <TestimonialCard
                        text="From complete beginner to competing in tournaments - HERO MMA made it possible. Amazing community!"
                        author="Sarah Williams"
                    />
                </div>
            </section>

            {/* VIDEO PLAYLIST SECTION */}
            <section className={`video-section fade-in-section ${visibleSections.includes(3) ? 'is-visible' : ''}`} id="gallery">
                <h2 className="section-title">TESTIMONIAL PLAYLIST</h2>
                <div className="video-playlist">
                    <VideoItem title="You get what you work for..." duration="03:45" />
                    <VideoItem title="Let's go girls" duration="02:30" />
                    <VideoItem title="The youngest fighter to attend..." duration="04:15" />
                    <VideoItem title="The Hero who is not a number" duration="05:20" />
                </div>
            </section>

            {/* FOOTER */}
            <footer className="footer" id="contact">
                <h3>Your Health Is Our Responsibility</h3>
                <a href="#" className="contact-btn">Contact Us</a>
            </footer>
        </div>
    );
}

// Service Card Component
const ServiceCard = ({ title, description, gradient }) => {
    const [isHovered, setIsHovered] = useState(false);

    return (
        <div
            className="service-card"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
        >
            <div className="service-image">
                <div
                    className="service-placeholder"
                    style={{ background: gradient, display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', color: '#666' }}
                >
                    {title} Image
                </div>
                <div className={`service-overlay ${isHovered ? 'visible' : ''}`}>
                    <div style={{ padding: '40px', textAlign: 'center' }}>
                        <p style={{ fontSize: '16px', lineHeight: '1.8', color: '#ddd' }}>{description}</p>
                    </div>
                </div>
            </div>
            <div className="service-content">
                <h3 className="service-name">{title.split(' ').join('\n')}</h3>
                <span className="service-arrow">→</span>
            </div>
        </div>
    );
};

// Testimonial Card Component
const TestimonialCard = ({ text, author }) => {
    return (
        <div className="testimonial-card">
            <p className="testimonial-text">"{text}"</p>
            <p className="testimonial-author">- {author}</p>
        </div>
    );
};

// Video Item Component
const VideoItem = ({ title, duration }) => {
    return (
        <div className="video-item">
            <div className="play-icon">▶</div>
            <div className="video-info">
                <h4>{title}</h4>
                <span className="video-duration">{duration}</span>
            </div>
        </div>
    );
};

export default App;
